class Observable {

  observers = [];

  subscribe(observer) {
    this.observers.push(observer);
  }

  unsubscribe(observer) {
    this.observers.filter((o) => o !== observer);
  }

  notify(payload) {
    this.observers.forEach((observer) => observer(payload));
  }
}

export default Observable;
